  const [data, setData] = useState({
    temparature: 300.15,
    city: "ANTARCTICA",
    country: "",
    Discription: "snow",
    humidity: 100,
    pressure: 1050,
    speed: 3.44,
  });



  const [city, setCity] = useState("");
  const handle = () => {
    if (city !== "") {
      let apiUrl = `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=0afba858d74acf07dcdb6993e6fbd8d4&unit=metric`;
